import React, {Component} from 'react';
import DataTable from 'react-data-table-component';
import { API_SERVER, API_PORT } from '../Config/credentials';



export default class Records extends Component {

    state = {
        data: []
    };

    constructor(props){
        super(props);

        this.columns = [
            {
                name: 'Timestamp',
                selector: row => row.timestamp
            },
            {
                name: 'Left',
                selector: row => row.left,
            },
            {
                name: 'Right',
                selector: row => row.right,
            },
            {
                name: 'Operations',
                selector: row => row.operator,
            },
            {
                name: 'Result',
                selector: row => "hoy",
            },

        ];

        this.data1 = [{
            left: 10,
            right: 20,
            operator: "+",
            timestamp: "Hoy"
        }];

        this.getRecordsFromAPI().then((result) => {
            let opList = [];
            result.forEach((item) => {

                opList.push({
                    left: item.left,
                    right: item.right,
                    operator: item.operator,
                    timestamp: item.timestamp
                });

                //this.setState({
                  //  data: opList
                //});
            });
        });
    }



    componentDidMount(){
        this.props.setLocalHandler(this.addLocalOperation);
    }

    getRecordsFromAPI = async () => {
        const requestOptions = {
            method: 'GET',
            headers: { 'Content-Type':'application/json'}
        }

        const response = await fetch(`${API_SERVER}:${API_PORT}/getRecords/`, requestOptions);
        const result = await response.json();
        return result;
    }

    addLocalOperation = (newOperation) => {
        console.log(this.state.data);

    }

    render(){
        return(
            <div>
                <DataTable columns={this.columns} data={this.data1}/>
            </div>
        );
    }
}
